$numPosts = 25;

function GetPosts($catID, $idxID)
{
  global $links;
  $i = 0;
  $posts = array();
  
  foreach($links as $post)
  {
    if(($post["catId"] == catID) && ($post["idxId"] == idxID))
    {
      $posts[i] = $post;
      $i++;
    }
  }
  
  return $posts;
}

function GetPost($postID)
{
  if(($postID != 0) && ($postID <= $numPosts))
  {
    return $links[$postID];
  }
  else
  {
    return $links[0];
  }
}

